(()=>{"use strict";let e=["Home","Menu","Contact"],t=document.querySelector("#content"),o=document.createElement("ul");for(let t=0;t<3;t++){let n=document.createElement("li");n.textContent=e[t],o.appendChild(n)}t.appendChild(o);let n=document.querySelectorAll("#content li");function c(e){switch(console.log(e.target.textContent),e.target.textContent){case"Home":console.log("This is home");break;case"Contact":console.log("This is contact");break;case"Menu":console.log("This is menu")}}Array.from(n).forEach((e=>{e.addEventListener("click",c)}))})();